<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h3 class="text-info">Tambah Data Armada</h3>		</div>		<div class="well">			<form class="form-horizontal">			<legend>Detail Armada</legend>				<div class="control-group">					<label class="control-label" for="inputNomorUnit">Nomor Unit</label>					<div class="controls">						<input type="text" name="inputNomorUnit" class="span2">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputMerek">Merek</label>					<div class="controls">						<input type="text" name="inputMerek" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputTipe">Type</label>					<div class="controls">						<input type="text" name="inputTipe" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputNomorPolisi">Nomor Polisi</label>					<div class="controls">						<input type="text" name="inputNomorPolisi" class="span2">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputNomorRangka">Nomor Rangka</label>					<div class="controls">						<input type="text" name="inputNomorRangka" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputNomorMesin">Nomor Mesin</label>					<div class="controls">						<input type="text" name="inputNomorMesin" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputSTNK">Nomor STNK</label>					<div class="controls">						<input type="text" name="inputSTNK" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputSTNKEpire">STNK Expire</label>					<div class="controls">						<input id="inputSTNKExpire" type="text" class="span3"> 					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputOperasional">Wilayah Operasi</label>					<div class="controls">						<select name="inputOperasional" class="span3">							<option value="umum">Umum</option>							<option value="bandara">Bandara</option>						</select>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputKondisi">Kondisi</label>					<div class="controls">						<select name="inputKondisi" class="span3">							<option value="ready">Siap Operasi</option>							<option value="trouble">Trouble</option>						</select>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputTglGabung">Tanggal Gabung</label>					<div class="controls">						<input id="inputTglGabung" type="text" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputDeskripsi">Keterangan/Deskripsi</label>					<div class="controls">						<textarea name="inputDeskripsi" rows="3"></textarea>					</div>				</div>			<br><br>			<legend>Kepemilikan Armada</legend>				<div class="control-group">					<label class="control-label" for="inputPemilik">Pilih Kepemilikan</label>					<div class="controls">						<input type="hidden" name="inputPemilik" value="0">					    <div class="btn-group" data-toggle="buttons-radio">						    <button type="button" class="btn btn-primary" onClick="OwnerIs3G()">PT. Gempita Gemintang Gemilang</button>						    <button type="button" class="btn btn-primary" onClick="OwnerIsPD()">PD. Gowata Taxi</button>						    <button type="button" class="btn btn-primary" onClick="OwnerIsMN()">3G - Multi Niaga</button>					    </div>					</div>				</div>			<br><br>			<legend>Target</legend>				<div class="control-group">					<label class="control-label" for="inputTargetSetoran">Target Setoran</label>					<div class="controls">						<span class="add-on">Rp</span>						<input type="text" name="inputTargetSetoran" class="span2" onkeyup="formatNumber(this)">						<br>						<small>Cukup ketikkan digit angka (tidak termasuk "titik" dan "koma")</small>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputTargetCaper">Target Cadangan Perawatan</label>					<div class="controls">						<span class="add-on">Rp</span>						<input type="text" name="inputTargetCaper" class="span2" onkeyup="formatNumber(this)">						<br>						<small>Cukup ketikkan digit angka (tidak termasuk "titik" dan "koma")</small>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputSaldoCaper">SaldoCadangan Perawatan</label>					<div class="controls">						<input type="hidden" name="posisiSaldoCaper" value="">						<div class="btn-group" data-toggle="buttons-radio">						    <button type="button" class="btn btn-mini btn-primary" onClick="isPositif()">+</button>						    <button type="button" class="btn btn-mini btn-primary" onClick="isNegatif()">-</button>					    </div>						<span class="add-on">Rp</span>						<input type="text" name="inputSaldoCaper" class="span2" onkeyup="formatNumber(this)">						<br>						<small>Cukup ketikkan digit angka (tidak termasuk "titik" dan "koma")</small>					</div>				</div>			</form>			<br><br>			<div class="info"></div>			<div class="pull-right"><button class="btn btn-success" onClick="saveData()">Tambahkan Armada Baru</button></div>			<br><br>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#inputSTNKExpire').datepicker({dateFormat : "yy-mm-dd", changeMonth : true, changeYear : true});		$('#inputTglGabung').datepicker({dateFormat : "yy-mm-dd", changeMonth : true, changeYear : true});	});	function OwnerIs3G()	{		$('input[name=inputPemilik]').val(1);	}	function OwnerIsPD()	{		$('input[name=inputPemilik]').val(2);	}	function OwnerIsMN()	{		$('input[name=inputPemilik]').val(3);	}	function isPositif()	{		$('input[name=posisiSaldoCaper]').val("positif");	}	function isNegatif()	{		$('input[name=posisiSaldoCaper]').val("negatif");	}	function saveData()	{		var nomorunit = $('input[name=inputNomorUnit]').val();		var merek = $('input[name=inputMerek]').val();		var tipe = $('input[name=inputTipe]').val();		var	nomorpolisi = $('input[name=inputNomorPolisi]').val();		var nomorrangka = $('input[name=inputNomorRangka]').val();		var	nomormesin = $('input[name=inputNomorMesin]').val();		var stnk = $('input[name=inputSTNK]').val();		var stnkexpire = $('#inputSTNKExpire').val();		var operasional = $('select[name=inputOperasional]').val();		var kondisi = $('select[name=inputKondisi]').val();		var tglgabung = $('#inputTglGabung').val();		var deskripsi = $('textarea[name=inputDeskripsi]').val();		var pemilik = $('input[name=inputPemilik]').val();		var targetsetoran = $('input[name=inputTargetSetoran]').val();		targetsetoran = parseInt(replaceAll(targetsetoran,',',''));		var targetcaper = $('input[name=inputTargetCaper]').val();		targetcaper = parseInt(replaceAll(targetcaper,',',''));		var posisiSaldoCaper = $('input[name=posisiSaldoCaper]').val();		if(posisiSaldoCaper == 'positif')		{			var saldocaper = $('input[name=inputSaldoCaper]').val();			saldocaper = parseInt(replaceAll(saldocaper,',',''));		}		else		{			var saldocaper = $('input[name=inputSaldoCaper]').val();			saldocaper = parseInt(replaceAll(saldocaper,',',''));			saldocaper *= -1;		}				if(nomorunit == '' || operasional == '' || kondisi == '' || pemilik == 0 || targetsetoran == 0 || targetcaper == 0)		{			$('.info').html("<div class='alert alert-error'><h4>Upppss!!</h4>Mohon Isi dengan lengkap..</div>");		}		else		{			$.post(site+'/armada/save',				{nomorunit : nomorunit,					merek : merek,					tipe : tipe,					nomorpolisi : nomorpolisi,					nomorrangka : nomorrangka,					nomormesin : nomormesin,					stnk : stnk,					stnkexpire : stnkexpire,					operasional : operasional,					kondisi : kondisi,					tglgabung : tglgabung,					deskripsi : deskripsi,					pemilik : pemilik,					targetsetoran : targetsetoran,					targetcaper : targetcaper,					saldocaper : saldocaper}, 				function(){					window.location = site+"armada";				});		}	}</script>