<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h3 class="text-info">Update Data Armada</h3>		</div>		<div class="well">			<form class="form-horizontal">				<input type="hidden" name="inputID" value="<? echo $this->detailArmada['id']; ?>"> 				<div class="control-group">					<label class="control-label" for="inputNomorUnit">Nomor Unit</label>					<div class="controls">						<input type="text" name="inputNomorUnit" class="span2" value="<? echo $this->detailArmada['nomorunit']; ?>">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputMerek">Merek</label>					<div class="controls">						<input type="text" name="inputMerek" class="span3" value="<? echo $this->detailArmada['merek']; ?>">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputTipe">Type</label>					<div class="controls">						<input type="text" name="inputTipe" class="span3" value="<? echo $this->detailArmada['tipe']; ?>">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputNomorPolisi">Nomor Polisi</label>					<div class="controls">						<input type="text" name="inputNomorPolisi" class="span2" value="<? echo $this->detailArmada['nomorpolisi']; ?>">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputNomorRangka">Nomor Rangka</label>					<div class="controls">						<input type="text" name="inputNomorRangka" class="span3" value="<? echo $this->detailArmada['nomorrangka']; ?>">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputNomorMesin">Nomor Mesin</label>					<div class="controls">						<input type="text" name="inputNomorMesin" class="span3" value="<? echo $this->detailArmada['nomormesin']; ?>">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputSTNK">Nomor STNK</label>					<div class="controls">						<input type="text" name="inputSTNK" class="span3" value="<? echo $this->detailArmada['stnk']; ?>">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputSTNKEpire">STNK Expire</label>					<div class="controls">						<input id="inputSTNKExpire" type="text" class="span3" value="<? echo $this->detailArmada['stnkexpire']; ?>">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputOperasional">Wilayah Operasi</label>					<div class="controls">						<select name="inputOperasional" class="span3">							<option value="umum">Umum</option>							<option value="bandara">Bandara</option>						</select>						<script type="text/javascript">							$('selec[name=inputOperasional]').val("<? echo $this->detailArmada['operasional']; ?>");						</script>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputKondisi">Kondisi</label>					<div class="controls">						<select name="inputKondisi" class="span3">							<option value="ready">Siap Operasi</option>							<option value="trouble">Trouble</option>						</select>						<script type="text/javascript">							$('selec[name=inputKondisi]').val("<? echo $this->detailArmada['kondisi']; ?>");						</script>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputTglGabung">Tanggal Gabung</label>					<div class="controls">						<input id="inputTglGabung" type="text" class="span3" value="<? echo $this->detailArmada['tglgabung']; ?>">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputDeskripsi">Keterangan/Deskripsi</label>					<div class="controls">						<textarea name="inputDeskripsi" rows="3"><? echo $this->detailArmada['deskripsi']; ?></textarea>					</div>				</div>			</form>			<br><br>			<div class="info"></div>			<div class="pull-right"><button class="btn btn-success" onClick="saveData()">Simpan Perubahan</button></div>			<br><br>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#inputSTNKExpire').datepicker({dateFormat : "yy-mm-dd", changeMonth : true, changeYear : true});		$('#inputTglGabung').datepicker({dateFormat : "yy-mm-dd", changeMonth : true, changeYear : true});	});	function saveData()	{		var id = $('input[name=inputID]').val();		var nomorunit = $('input[name=inputNomorUnit]').val();		var merek = $('input[name=inputMerek]').val();		var tipe = $('input[name=inputTipe]').val();		var	nomorpolisi = $('input[name=inputNomorPolisi]').val();		var nomorrangka = $('input[name=inputNomorRangka]').val();		var	nomormesin = $('input[name=inputNomorMesin]').val();		var stnk = $('input[name=inputSTNK]').val();		var stnkexpire = $('#inputSTNKExpire').val();		var operasional = $('select[name=inputOperasional]').val();		var kondisi = $('select[name=inputKondisi]').val();		var tglgabung = $('#inputTglGabung').val();		var deskripsi = $('textarea[name=inputDeskripsi]').val();		if(nomorunit == '' || operasional == '' || kondisi == '')		{			$('.info').html("<div class='alert alert-error'><h4>Upppss!!</h4>Mohon Isi dengan lengkap..</div>");		}		else		{			$.post(site+'/armada/updatedetail',				{id : id, 					nomorunit : nomorunit,					merek : merek,					tipe : tipe,					nomorpolisi : nomorpolisi,					nomorrangka : nomorrangka,					nomormesin : nomormesin,					stnk : stnk,					stnkexpire : stnkexpire,					operasional : operasional,					kondisi : kondisi,					tglgabung : tglgabung,					deskripsi : deskripsi}, 				function(){					load('armada/detail/'+id, '#content');				});		}	}</script>