<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h1 class="text-info">Data Armada</h1>		</div>		<div class="well">			<div class="pull-right"><button class="btn btn-primary" onClick="load('armada/formaddnew','#content')">Tambah Armada Baru</button></div>			<input type="text" id="inputNomorUnit" class="span3" data-provide="typeahead" placeholder="Cari Armada">			<table class="table table-bordered table-hover">				<thead>					<tr>						<th>Nomor Unit</th>						<th>Merek</th>						<th>Tipe</th>						<th>Nomor Polisi</th>						<th>Wilayah Operasi</th>						<th>Kondisi</th>						<th width='15%'>Aksi</th>					</tr>				</thead>								<tbody>					<? foreach ($this->listArmada as $key => $value) { ?>						<tr id="armada-<? echo $value['id']; ?>">							<td><span class='label label-inverse'><? echo $value['nomorunit']; ?></span></td>							<td><? echo $value['merek']; ?></td>							<td><? echo $value['tipe']; ?></td>							<td><? echo $value['nomorpolisi']; ?></td>							<td><span class='label label-info'><? echo ucwords($value['operasional']); ?></span></td>							<td>								<? echo $value['kondisi'] == "ready" ? 									"<span class='label label-success'>Siap Operasi</span>" : 									"<span class='label label-important'>Trouble"; ?>							</td>							<td>								<div class="btn-group">									<a class="btn btn-primary" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>									<a class="btn btn-primary dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>									<ul class="dropdown-menu">										<li>											<a href="javascript:void(0)" onClick="return details(<? echo $value['id']; ?>)">											<i class="icon-list-alt"></i> Details</a>										</li>										<li>											<a href="javascript:void(0)" onClick="return remove(<? echo $value['id']; ?>)">											<i class="icon-remove"></i> Hapus</a>										</li>									</ul>								</div>							</td>						</tr>					<? } ?>				</tbody>			</table>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#inputNomorUnit').typeahead({			source : function(query,process){				return $.post(site+'sro/getlistarmada', {nomor : query}, function(response){					response = $.parseJSON(response);					var sourceArr = [];						for(var i = 0; i < response.length; i++)					{						sourceArr.push(response[i].id +"#"+ response[i].nomorunit +"#"+ response[i].kondisi);					}					return process(sourceArr);				})			},			highlighter : function(item){				var armada = item.split('#');				var item = ''                		+ "<div class='typeahead_wrapper'>"                		+ "<img class='typeahead_photo' src='"+site+"assets/img/car-icon.png'/>"                		+ "<div class='typeahead_labels'>"                		+ "<div class='typeahead_primary'>" + armada[1] + "</div>"                 		+ "<div class='typeahead_secondary'>Kondisi : " + capitalizeFirstLetter(armada[2]) + "</div>"                 		+ "</div>"                 		+ "</div>";       		 	return item;			},			updater : function(item){				var armada = item.split('#');				load('armada/detail/'+armada[0],'#content');			}		});	})	function details(id)	{		load('armada/detail/'+id, '#content');	}	function remove(id)	{		if(confirm("Anda Yakin Akan Menghapus Unit Ini? Semua Data Berkaitan dengan Unit ini Akan Ikut Terhapus."))		{			$.post(site+'armada/delete', {id : id}, function(){				$('#armada-'+id).remove();			});		}	}</script>