<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="form-signin">	        <h2 class="form-signin-heading muted">Halaman Login</h2>	        <input name="inputUserName" type="text" class="input-block-level" placeholder="Nama User">	        <input name="inputPassword" type="password" class="input-block-level" placeholder="Password">	        <label class="checkbox">	          <input type="checkbox" value="remember-me"> Ingat Saya	        </label>	        <button class="btn btn-primary" onClick="loginUser()">Masuk</button>	        <br><br>	        <div id="loginInfo" class="text-error"></div>      	</div>	</div></div><script type="text/javascript">	function loginUser()	{		var username = $('input[name=inputUserName]').val();		var password = $('input[name=inputPassword]').val();		if(username !== '' && password !== '')		{			$.ajax({				type:'POST',				url:site+'index/login',				data:{username : username, password : password},				dataType:'json',				cache:false,				success:function(access)				{					if(access.status === 'GRANTED')					{						window.location = '<? echo URL; ?>';					}					else					{						$('#loginInfo').html("Username dan atau Password Salah");					}				}			});		}		else		{			$('#loginInfo').html("Masukkan Username dan Password Anda!");		}	}</script>