<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h3 class="text-info">Detail Data Kecelakaan</h3>		</div>		<div class="well span6">			<legend>Detail Kecelakaan</legend>			<table class="table table-hover">				<tbody>					<tr>						<td width="49%"><div class="pull-right">Nomor Laka</div></td>						<td>:</td>						<td width="49%"><strong class="text-info"><? echo $this->detailLaka['nomorlaka']; ?></strong></td>					</tr>					<tr>						<td width="49%"><div class="pull-right">Nomor Unit Armada</div></td>						<td>:</td>						<td width="49%"><strong class="text-info"><? echo $this->detailLaka['nomorunit']; ?></strong></td>					</tr>					<tr>						<td><div class="pull-right">Pengemudi</div></td>						<td>:</td>						<td><strong class="text-info"><? echo $this->detailLaka['namapengemudi']; ?></strong></td>					</tr>					<tr>						<td><div class="pull-right">Tanggal</div></td>						<td>:</td>						<td><strong class="text-info"><? echo $this->detailLaka['tanggal']; ?></strong></td>					</tr>					<tr>						<td><div class="pull-right">Kejadian</div></td>						<td>:</td>						<td><strong class="text-info"><? echo $this->detailLaka['kejadian']; ?></strong></td>					</tr>					<tr>						<td><div class="pull-right">Biaya Spare Parts</div></td>						<td>:</td>						<td><strong class="text-info"><? echo $this->pecah($this->detailLaka['biayaparts']); ?></strong></td>					</tr>					<tr>						<td><div class="pull-right">Biaya Pengurusan</div></td>						<td>:</td>						<td><strong class="text-info"><? echo $this->pecah($this->detailLaka['biayaurus']); ?></strong></td>					</tr>					<tr>						<td><div class="pull-right">Biaya Lain-lain</div></td>						<td>:</td>						<td><strong class="text-info"><? echo $this->pecah($this->detailLaka['biayalain']); ?></strong></td>					</tr>					<tr>						<td><div class="pull-right">Nomor Tagihan Laka</div></td>						<td>:</td>						<td><strong class="text-info"><? echo $this->detailLoanLaka['nomorloan']; ?></strong></td>					</tr>				</tbody>			</table>			<center><button class="btn btn-mini btn-info" onClick="kembali()">Kembali</button></center>		</div>		<div class="well span5">			<legend>Detail Tagihan Laka</legend>			<table class="table table-hover">				<tbody>					<tr>						<td><div class="pull-right">Nomor Tagihan Laka</div></td>						<td>:</td>						<td><strong class="text-info"><? echo $this->detailLoanLaka['nomorloan']; ?></strong></td>					</tr>					<tr>						<td><div class="pull-right">Beban Tagihan Laka</div></td>						<td>:</td>						<td><strong class="text-info"><? echo $this->pecah($this->detailLoanLaka['bebanlaka']); ?></strong></td>					</tr>					<tr>						<td><div class="pull-right">Lunas</div></td>						<td>:</td>						<td><strong class="text-info"><? echo $this->detailLoanLaka['lunas'] == 1 ? "Lunas" : "Belum"; ?></strong></td>					</tr>				</tbody>			</table>			<? if($this->detailLoanLaka['lunas'] == 0) : ?>			<center>				<button class="btn btn-mini btn-info" onClick="lunasLoanLaka(<? echo $this->detailLoanLaka['id']; ?>,<? echo $this->detailLaka['id']; ?>)">Lunasi Tagihan</button>			</center>			<? endif; ?>		</div>	</div></div><script type="text/javascript">	function kembali()	{		window.location = site+"laka";	}	function lunasLoanLaka(id,laka)	{		if(confirm("Anda Yakin Akan Menandai Tagihan Ini Telah Lunas?\nPerubahan ini tidak bisa dikembalikan"))		{			$.post(site+'laka/lunasloanlaka',{id : id}, function(){				load('laka/detail/'+laka, '#content');			});		}	}</script>