<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h1 class="text-info">Data Kecelakaan</h1>		</div>		<div class="well">			<div class="pull-right"><button class="btn btn-primary" onClick="load('laka/formaddnew','#content')">Kejadian Baru</button></div>			<br><br>			<table class="table table-bordered table-hover">				<thead>					<tr>						<th>Nomor Laka</th>						<th>Tanggal</th>						<th>Armada</th>						<th>Pengemudi</th>						<th>Total Pengeluaran</th>						<th width='15%'>Aksi</th>					</tr>				</thead>								<tbody>					<? foreach ($this->listLaka as $key => $value) { ?>						<tr id="laka-<? echo $value['id']; ?>">							<td><? echo $value['nomorlaka']; ?></td>							<td><? echo date('d-m-Y', strtotime($value['tanggal'])); ?></td>							<td><? echo $value['nomorunit']; ?></td>							<td><? echo $value['namapengemudi']; ?></td>							<td><? echo $total = $this->pecah((int)$value['biayaparts']+(int)$value['biayaurus']+(int)$value['biayalain']); ?></td>							<td>								<div class="btn-group">									<a class="btn btn-primary" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>									<a class="btn btn-primary dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>									<ul class="dropdown-menu">										<li>											<a href="javascript:void(0)" onClick="return details(<? echo $value['id']; ?>)">											<i class="icon-list-alt"></i> Details</a>										</li>										<li>											<a href="javascript:void(0)" onClick="return remove(<? echo $value['id']; ?>)">											<i class="icon-remove"></i> Hapus</a>										</li>									</ul>								</div>							</td>						</tr>					<? } ?>				</tbody>			</table>		</div>	</div></div><script type="text/javascript">	function details(id)	{		load('laka/detail/'+id,'#content');	}	function remove(id)	{		if(confirm("Anda Yakin Akan Menghapus laka Ini? Semua Data Berkaitan Akan Ikut Terhapus."))		{			$.post(site+'laka/delete', {id : id}, function(){				$('#laka-'+id).remove();			});		}	}</script>