<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h1 class="text-info">Data Ownership</h1>		</div>		<div class="well">			<div class="pull-right"><button class="btn btn-primary" onClick="load('ownership/formaddnew','#content')">Request Ownership</button></div>			<br><br>			<table class="table table-bordered table-hover">				<thead>					<tr>						<th>Nomor</th>						<th>Pengemudi</th>						<th>Armada</th>						<th>Status</th>						<th>Selesai</th>						<th width='10%'>Aksi</th>					</tr>				</thead>								<tbody>					<? foreach ($this->listOwnership as $key => $value) { ?>						<tr id="ownership-<? echo $value['id']; ?>">							<td><? echo $value['nomor']; ?></td>							<td><? echo $value['nomorpengemudi'].' - '.$value['namapengemudi']; ?></td>							<td><? echo $value['nomorunit']; ?></td>							<td>								<? echo $value['approve'] == 1 ? 								"<span class='label label-success'>Approved</span>" : 								"<span class='label label-warning'>Pending</span>"; ?>							</td>							<td>								<? echo $value['finished'] == 1 ? 								"<span class='label label-success'>Selesai</span>" : 								"<span class='label label-warning'>Belum</span>"; ?>							</td>							<td>								<div class="btn-group">									<a class="btn btn-primary" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>									<a class="btn btn-primary dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>									<ul class="dropdown-menu">										<li>											<a href="javascript:void(0)" onClick="return details(<? echo $value['id']; ?>)">											<i class="icon-list-alt"></i> Details</a>										</li>										<? if($value['approve'] == 0) : ?>										<li>											<a href="javascript:void(0)" onClick="return approve(<? echo $value['id']; ?>)">											<i class="icon-ok"></i> Approve</a>										</li>										<? endif; ?>										<? if($value['finished'] == 0) : ?>										<li>											<a href="javascript:void(0)" onClick="return finished(<? echo $value['id']; ?>)">											<i class="icon-ok"></i> Set Selesai</a>										</li>										<? else: ?>										<li>											<a href="javascript:void(0)" onClick="return unfinished(<? echo $value['id']; ?>)">											<i class="icon-ok"></i> Set Belum Selesai</a>										</li>										<? endif; ?>										<li>											<a href="javascript:void(0)" onClick="return remove(<? echo $value['id']; ?>)">											<i class="icon-remove"></i> Hapus</a>										</li>									</ul>								</div>							</td>						</tr>					<? } ?>				</tbody>			</table>		</div>	</div></div><script type="text/javascript">	function details(id)	{		load('ownership/detail/'+id, '#content');	}	function approve(id)	{		$.post(site+'ownership/approve', {id : id}, function(){			load('ownership/detail/'+id, '#content');		});	}	function finished(id)	{		$.post(site+'ownership/finished', {id : id}, function(){			load('ownership/detail/'+id, '#content');		});	}	function unfinished(id)	{		$.post(site+'ownership/unfinished', {id : id}, function(){			load('ownership/detail/'+id, '#content');		});	}	function remove(id)	{		if(confirm("Anda Yakin Akan Menghapus Pegawai Ini? Semua Data Berkaitan dengan Pegawai ybs Akan Ikut Terhapus."))		{			$.post(site+'ownership/delete', {id : id}, function(){				$('#ownership-'+id).remove();			});		}	}</script>