<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h1 class="text-info">Purchase Order Spare Parts</h1>		</div>		<div class="well">			<div class="pull-right">				<button class="btn btn-primary" onClick="showFormAddNew()"><i class="icon-plus icon-white"></i> Buat PO</button>			</div>			<p>Petunjuk Membuat PO:</p>			<small>				<p>Klik Tombol Buat PO</p>				<p>Masukkan Pesan Pada Form Yang Tampil, Bukan Item Spare Parts</p>				<p>Klik Detail Pada Kolom Aksi Pada Item PO Yang Baru Anda Buat</p>				<p>Selanjutnya Akan Tampil Detail PO. Barulah Di Halaman Ini anda Memilih Item Spare Parts yang Masuk Daftar PO</p>				<p>Caranya adalah dengan Klik Tombol Tambah Item</p>			</small>			<table class="table table-bordered table-hover">				<thead>					<tr>						<th>Nomor Order</th>						<th width="10%">Tgl Order</th>						<th>Yang Mengajukan</th>						<th width="30%">Keterangan</th>						<th>Status</th>						<th width='10%'>Aksi</th>					</tr>				</thead>								<tbody id="tblparts">					<? foreach ($this->listOrder as $key => $value) { ?>						<tr id="order-<? echo $value['id']; ?>">							<td><? echo $value['nomororder']; ?></td>							<td><? echo date('d-m-Y', strtotime($value['tglorder'])); ?></td>							<td><? echo $value['namapegawai']; ?></td>							<td><? echo $value['keterangan']; ?></td>							<td>								<? echo $value['approve'] == 1 ? 								"<span class='label label-success'>Approved</span>" : 								"<span class='label label-warning'>Pending</span>"; ?>							</td>							<td>								<div class="btn-group">									<a class="btn btn-primary" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>									<a class="btn btn-primary dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>									<ul class="dropdown-menu">										<li>											<a href="javascript:void(0)" onClick="return showDetailOrder(<? echo $value['id']; ?>)">											<i class="icon-pencil"></i> Detail Item</a>										</li>										<? if($value['approve'] == 0) : ?>										<li>											<a href="javascript:void(0)" onClick="return approve(<? echo $value['id']; ?>)">											<i class="icon-ok"></i> Approve</a>										</li>										<? endif; ?>										<li>											<a href="javascript:void(0)" onClick="return remove(<? echo $value['id']; ?>)">											<i class="icon-remove"></i> Hapus</a>										</li>									</ul>								</div>							</td>						</tr>					<? } ?>				</tbody>			</table>		</div>	</div></div><!-- Form Add Order Parts [modal]===================================== --><div id="formAddNew" class="modal hide fade" tabindex="-1" aria-hidden="true">	<div class="modal-header">		<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>		<h3 id="myModalLabel">Purchase Order</h3>	</div>	<div class="modal-body">		<form class="form-horizontal">			<div class="control-group">				<label class="control-label" for="inputKeterangan">Masukkan Pesan Order</label>				<div class="controls">					<textarea id="inputKeterangan"></textarea>				</div>			</div>						</form>	</div>	<div class="modal-footer">		<button class="btn" data-dismiss="modal" aria-hidden="true">Batal</button>		<button class="btn btn-primary" onClick="addNew()" data-dismiss="modal" aria-hidden="true">Simpan</button>	</div></div><!-- End of Form Add Parts [modal] --><script type="text/javascript">	function showFormAddNew()	{		$('#formAddNew').modal('show');	}	function addNew()	{			var keterangan = $('#inputKeterangan').val();		if(confirm("Sistem Akan Menerbitkan Purchase Order Dengan Menandai Anda Sebagai Pengusul"))		{			$.post(site+'parts/addneworder', {keterangan : keterangan}, function(){				window.location = site+"parts/order";			});		}	}	function showDetailOrder(id)	{		load('parts/detailorder/'+id, '#content');	}	function approve(id)	{		$.post(site+'parts/approveorder', {id : id}, function(){				window.location = site+"parts/order";			});	}	function remove(id)	{		if(confirm("Anda Yakin Akan Menghapus Data Ini? \nSemua Data Berkaitan dengan Data ini Akan Ikut Terhapus."))		{			$.post(site+'parts/delete', {id : id}, function(){				$('#order-'+id).remove();			});		}	}</script>