<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h1 class="text-info">Detail PO [<? echo $this->detailPencairan['nomororder']; ?>]</h1>		</div>		<div class="well">			<input type="hidden" id="poID" value="<? echo $this->detailOrder['id']; ?>"> 			<table class="table table-bordered table-hover">				<thead>					<tr>						<th>Kode Spare Parts</th>						<th>Nama Spare Parts</th>						<th>Satuan</th>						<th>Jumlah</th>					</tr>				</thead>									<tbody id="tblparts">					<? foreach ($this->listDetailPencairan as $key => $value) { ?>						<tr id="item-<? echo $value['id']; ?>">							<input type="hidden" id="partsID-<? echo $value['id']; ?>" value="<? echo $value['parts']; ?>">							<input type="hidden" id="jumlah-<? echo $value['id']; ?>" value="<? echo $value['jumlah']; ?>">							<td><? echo $value['kode']; ?></td>							<td><? echo $value['nama']; ?></td>							<td><? echo $value['satuan']; ?></td>							<td><? echo $value['jumlah']; ?></td>						</tr>					<?  } ?>				</tbody>			</table>		</div>	</div></div><!-- Form Add Parts [modal]===================================== --><div id="formAddNew" class="modal hide fade" tabindex="-1" aria-hidden="true">	<div class="modal-header">		<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>		<h3 id="myModalLabel">Tambah Spare Parts</h3>	</div>	<div class="modal-body">		<form class="form-horizontal">			<div class="control-group">				<label class="control-label" for="inputParts">Spare Parts</label>				<div class="controls">					<select id="inputParts">						<option value="0">--Pilih</option>						<? foreach ($this->listParts as $key => $value) { ?>							<option value="<? echo $value['id']; ?>"><? echo $value['kode'].'-'.$value['namaparts']; ?></option>						<? } ?>					</select>				</div>			</div>				<div class="control-group">				<label class="control-label" for="inputJumlah">Jumlah</label>				<div class="controls">					<input id="inputJumlah" class="span2" type="text" value="0">				</div>			</div>			</form>	</div>	<div class="modal-footer">		<button class="btn" data-dismiss="modal" aria-hidden="true">Batal</button>		<button class="btn btn-primary" onClick="addNewItem()" data-dismiss="modal" aria-hidden="true">Simpan</button>	</div></div><!-- End of Form Add Parts [modal] --><!-- Form Update Parts [modal]===================================== --><div id="formUpdate" class="modal hide fade" tabindex="-1" aria-hidden="true">	<div class="modal-header">		<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>		<h3 id="myModalLabel">Update Spare Parts</h3>	</div>	<div class="modal-body">		<form class="form-horizontal">			<div class="control-group">				<label class="control-label" for="inputUParts">Spare Parts</label>				<div class="controls">					<input type="hidden" id="detailOrderID"> 					<select id="inputUParts">						<option value="0">--Pilih</option>						<? foreach ($this->listParts as $key => $value) { ?>							<option value="<? echo $value['id']; ?>"><? echo $value['kode'].'-'.$value['namaparts']; ?></option>						<? } ?>					</select>				</div>			</div>				<div class="control-group">				<label class="control-label" for="inputUJumlah">Jumlah</label>				<div class="controls">					<input id="inputUJumlah" class="span2" type="text" value="0">				</div>			</div>			</form>	</div>	<div class="modal-footer">		<button class="btn" data-dismiss="modal" aria-hidden="true">Batal</button>		<button class="btn btn-primary" onClick="updateItem()" data-dismiss="modal" aria-hidden="true">Simpan</button>	</div></div><!-- End of Form Update Parts [modal] --><script type="text/javascript">	function showFormAddNew()	{		$('#formAddNew').modal('show');	}	function showFormUpdate(id)	{		$('#formUpdate').modal('show');		$('#detailOrderID').val(id);		$('#inputUParts').val($('#partsID-'+id).val());		$('#inputUJumlah').val($('#jumlah-'+id).val());	}	function addNewItem()	{		var orderparts = $('#poID').val();		var parts = $('#inputParts').val();		var jumlah = $('#inputJumlah').val();		$.post(site+'parts/adddetailorder', {orderparts : orderparts, parts : parts, jumlah : jumlah}, function()		{			load('parts/detailorder/'+orderparts, '#content');		});	}	function updateItem()	{		var orderparts = $('#poID').val();		var id = $('#detailOrderID').val();		var parts = $('#inputUParts').val();		var jumlah = $('#inputUJumlah').val();		$.post(site+'parts/updatedetailorder', {id : id, parts : parts, jumlah : jumlah}, function()		{			load('parts/detailorder/'+orderparts, '#content');		});	}	function remove(id)	{		if(confirm("Anda Yakin Akan Menghapus Item Ini?"))		{			$.post(site+'parts/removedetailorder', {id : id}, function(){				$('#item-'+id).remove();			});		}	}</script>