<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h3 class="text-info">Tambah Data Pegawai</h3>		</div>		<div class="well">			<form class="form-horizontal">			<legend>Detail Pegawai</legend>				<div class="control-group">					<label class="control-label" for="inputNomor">Nomor/ID Pegawai</label>					<div class="controls">						<input type="text" name="inputNomor" class="span2">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputNama">Nama Lengkap</label>					<div class="controls">						<input type="text" name="inputNama" class="span4">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputAlamat">Alamat</label>					<div class="controls">						<input type="text" name="inputAlamat" class="span5">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputStatus">Status</label>					<div class="controls">						<select name="inputStatus" class="span2">							<option value="Belum Menikah">Belum Menikah</option>							<option value="Menikah">Menikah</option>							<option value="Duda">Duda</option>						</select>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputTglLahir">Tanggal Lahir</label>					<div class="controls">						<input id="inputTglLahir" type="text" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputAgama">Agama</label>					<div class="controls">						<input type="text" name="inputAgama" class="span2">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputPendidikan">Pendidikan</label>					<div class="controls">						<select name="inputPendidikan" class="span2">							<option value="Tamat SD">Tamat SD</option>							<option value="Tamat SMP">Tamat SMP</option>							<option value="Tamat SMA">Tamat SMA</option>							<option value="Tamat D1">Tamat D1</option>							<option value="Tamat D2">Tamat D2</option>							<option value="Tamat D3">Tamat D3</option>							<option value="Tamat D4/S1">Tamat D4/S1</option>						</select>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputPosisi">Posisi</label>					<div class="controls">						<input type="text" name="inputPosisi" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputJabatan">Jabatan</label>					<div class="controls">						<input type="text" name="inputJabatan" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputTglGabung">Tanggal Gabung</label>					<div class="controls">						<input id="inputTglGabung" type="text" class="span3">					</div>				</div>			</form>			<br><br>			<div class="info"></div>			<div class="pull-right"><button class="btn btn-success" onClick="saveData()">Tambahkan Pegawai Baru</button></div>			<br><br>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#inputTglLahir').datepicker({dateFormat : "yy-mm-dd", changeMonth : true, changeYear : true});		$('#inputTglGabung').datepicker({dateFormat : "yy-mm-dd", changeMonth : true, changeYear : true});	})	function tetap()	{		$('input[name=inputTipe]').val('Tetap');	}	function cadangan()	{		$('input[name=inputTipe]').val('Cadangan');	}	function saveData()	{		var nomorpegawai = $('input[name=inputNomor]').val();		var nama = $('input[name=inputNama]').val();		var alamat = $('input[name=inputAlamat]').val();		var status = $('select[name=inputStatus]').val();		var tgllahir = $('#inputTglLahir').val();		var agama = $('input[name=inputAgama]').val();		var pendidikan = $('select[name=inputPendidikan]').val();		var posisi = $('input[name=inputPosisi]').val();		var jabatan = $('input[name=inputJabatan]').val();		var tglgabung = $('#inputTglGabung').val();		if(nomorpegawai == '' || nama == '')		{			$('.info').html("<div class='alert alert-error'><h4>Upppss!!</h4>Mohon Isi dengan lengkap..</div>");		}		else		{			$.post(site+'/pegawai/save',				{nomorpegawai : nomorpegawai,				nama : nama,				alamat : alamat,				status : status,				tgllahir : tgllahir,				agama : agama,				pendidikan : pendidikan,				posisi : posisi,				jabatan : jabatan,				tglgabung : tglgabung}, 				function(){					window.location = site+"pegawai";				});		}	}</script>