<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h1 class="text-info">Data Pegawai</h1>		</div>		<div class="well">			<div class="pull-right"><button class="btn btn-primary" onClick="load('pegawai/formaddnew','#content')">Tambah Pegawai Baru</button></div>			<br><br>			<table class="table table-bordered table-hover">				<thead>					<tr>						<th>ID Pegawai</th>						<th>Nama Lengkap</th>						<th>Alamat</th>						<th>Posisi</th>						<th>Jabatan</th>						<th>TGL Gabung</th>						<th width='15%'>Aksi</th>					</tr>				</thead>								<tbody>					<? foreach ($this->listPegawai as $key => $value) { ?>						<tr id="pegawai-<? echo $value['id']; ?>">							<td><? echo $value['nomorpegawai']; ?></td>							<td><? echo $value['nama']; ?></td>							<td><? echo $value['alamat']; ?></td>							<td><? echo $value['posisi']; ?></td>							<td><? echo $value['jabatan']; ?></td>							<td><? echo date('d-m-Y', strtotime($value['tglgabung'])) ?></td>							<td>								<div class="btn-group">									<a class="btn btn-primary" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>									<a class="btn btn-primary dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>									<ul class="dropdown-menu">										<li>											<a href="javascript:void(0)" onClick="return details(<? echo $value['id']; ?>)">											<i class="icon-list-alt"></i> Details</a>										</li>										<li>											<a href="javascript:void(0)" onClick="return remove(<? echo $value['id']; ?>)">											<i class="icon-remove"></i> Hapus</a>										</li>									</ul>								</div>							</td>						</tr>					<? } ?>				</tbody>			</table>		</div>	</div></div><script type="text/javascript">	function details(id)	{		load('pegawai/detail/'+id, '#content');	}	function remove(id)	{		if(confirm("Anda Yakin Akan Menghapus Pegawai Ini? Semua Data Berkaitan dengan Pegawai ybs Akan Ikut Terhapus."))		{			$.post(site+'pegawai/delete', {id : id}, function(){				$('#pegawai-'+id).remove();			});		}	}</script>