<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h3 class="text-info">Tambah Data Pengemudi</h3>		</div>		<div class="well">			<form class="form-horizontal">			<legend>Detail Pengemudi</legend>				<div class="control-group">					<label class="control-label" for="inputNomor">Nomor/ID Pengemudi</label>					<div class="controls">						<input type="text" name="inputNomor" class="span2">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputNama">Nama Lengkap</label>					<div class="controls">						<input type="text" name="inputNama" class="span4">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputAlamat">Alamat</label>					<div class="controls">						<input type="text" name="inputAlamat" class="span5">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputStatus">Status</label>					<div class="controls">						<select name="inputStatus" class="span2">							<option value="Belum Menikah">Belum Menikah</option>							<option value="Menikah">Menikah</option>							<option value="Duda">Duda</option>						</select>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputTglLahir">Tanggal Lahir</label>					<div class="controls">						<input id="inputTglLahir" type="text" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputAgama">Agama</label>					<div class="controls">						<input type="text" name="inputAgama" class="span2">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputPendidikan">Pendidikan</label>					<div class="controls">						<select name="inputPendidikan" class="span2">							<option value="Tamat SD">Tamat SD</option>							<option value="Tamat SMP">Tamat SMP</option>							<option value="Tamat SMA">Tamat SMA</option>							<option value="Tamat D1">Tamat D1</option>							<option value="Tamat D2">Tamat D2</option>							<option value="Tamat D3">Tamat D3</option>							<option value="Tamat D4/S1">Tamat D4/S1</option>						</select>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputNomorKTP">Nomor KTP</label>					<div class="controls">						<input type="text" name="inputNomorKTP" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputKTPExpire">KTP Expire</label>					<div class="controls">						<input id="inputKTPExpire" type="text" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputJenisSIM">Golongan SIM</label>					<div class="controls">						<select name="inputJenisSIM" class="span3">							<option value="A Umum">SIM A Umum</option>							<option value="B1 Umum">SIM B1 Umum</option>							<option value="B2 Umum">SIM B2 Umum</option>						</select>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputNomorSIM">Nomor SIM</label>					<div class="controls">						<input type="text" name="inputNomorSIM" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputSIMExpire">SIM Expired</label>					<div class="controls">						<input id="inputSIMExpire" type="text" class="span3">					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputTipe">Golongan Pengemudi</label>					<div class="controls">						<input type="hidden" name="inputTipe">					    <div class="btn-group" data-toggle="buttons-radio">						    <button type="button" class="btn btn-primary" onClick="tetap()">Pengemudi Tetap</button>						    <button type="button" class="btn btn-primary" onClick="cadangan()">Pengemudi Cadangan</button>					    </div>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputTglGabung">Tanggal Gabung</label>					<div class="controls">						<input id="inputTglGabung" type="text" class="span3">					</div>				</div>			<br><br>			<legend>Kewajiban</legend>				<div class="control-group">					<label class="control-label" for="inputSaldoKS">Saldo Kurang Setoran (Jika Ada)</label>					<div class="controls">						<span class="add-on">Rp</span>						<input type="text" name="inputSaldoKS" class="span2" onkeyup="formatNumber(this)" value="0">						<br>						<small>Cukup ketikkan digit angka</small>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputSaldoLaka">Saldo Tagihan Laka (Jika Ada)</label>					<div class="controls">						<span class="add-on">Rp</span>						<input type="text" name="inputSaldoLaka" class="span2" onkeyup="formatNumber(this)" value="0">						<br>						<small>Cukup ketikkan digit angka</small>					</div>				</div>				<div class="control-group">					<label class="control-label" for="inputSaldoTabungan">Saldo Tabungan (Jika Ada)</label>					<div class="controls">						<span class="add-on">Rp</span>						<input type="text" name="inputSaldoTabungan" class="span2" onkeyup="formatNumber(this)" value="0">						<br>						<small>Cukup ketikkan digit angka</small>					</div>				</div>			</form>			<br><br>			<div class="info"></div>			<div class="pull-right"><button class="btn btn-success" onClick="saveData()">Tambahkan Pengemudi Baru</button></div>			<br><br>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#inputTglLahir').datepicker({dateFormat : "yy-mm-dd", changeMonth : true, changeYear : true});		$('#inputKTPExpire').datepicker({dateFormat : "yy-mm-dd", changeMonth : true, changeYear : true});		$('#inputSIMExpire').datepicker({dateFormat : "yy-mm-dd", changeMonth : true, changeYear : true});		$('#inputTglGabung').datepicker({dateFormat : "yy-mm-dd", changeMonth : true, changeYear : true});	});	function tetap()	{		$('input[name=inputTipe]').val('Tetap');	}	function cadangan()	{		$('input[name=inputTipe]').val('Cadangan');	}	function saveData()	{		var nomor = $('input[name=inputNomor]').val();		var nama = $('input[name=inputNama]').val();		var alamat = $('input[name=inputAlamat]').val();		var status = $('select[name=inputStatus]').val();		var tgllahir = $('#inputTglLahir').val();		var agama = $('input[name=inputAgama]').val();		var pendidikan = $('select[name=inputPendidikan]').val();		var nomorktp = $('input[name=inputNomorKTP]').val();		var expirektp = $('#inputKTPExpire').val();		var jenissim = $('select[name=inputJenisSIM]').val();		var nomorsim = $('input[name=inputNomorSIM]').val();		var simexpire = $('#inputSIMExpire').val();		var tipe = $('input[name=inputTipe]').val();		var tglgabung = $('#inputTglGabung').val();		var saldoks = $('input[name=inputSaldoKS]').val();		saldoks = parseInt(replaceAll(saldoks,',',''));		var saldoloanlaka = $('input[name=inputSaldoLaka]').val();		saldoloanlaka = parseInt(replaceAll(saldoloanlaka,',',''));		var saldotabungan = $('input[name=inputSaldoTabungan]').val();		saldotabungan = parseInt(replaceAll(saldotabungan,',',''));		if(nomor == '' || jenissim == '' || nama == '' || tipe == '')		{			$('.info').html("<div class='alert alert-error'><h4>Upppss!!</h4>Mohon Isi dengan lengkap..</div>");		}		else		{			$.post(site+'/pengemudi/save',				{nomor : nomor,				nama : nama,				alamat : alamat,				status : status,				tgllahir : tgllahir,				agama : agama,				pendidikan : pendidikan,				nomorktp : nomorktp,				expirektp : expirektp,				jenissim : jenissim,				nomorsim : nomorsim,				simexpire : simexpire,				tipe : tipe,				tglgabung : tglgabung,				saldoks : saldoks,				saldoloanlaka : saldoloanlaka,				saldotabungan : saldotabungan}, 				function(){					window.location = site+"pengemudi";				});		}	}</script>