<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h1 class="text-info">Data Pengemudi</h1>		</div>		<div class="well">			<div class="pull-right"><button class="btn btn-primary" onClick="load('pengemudi/formaddnew','#content')">Tambah Pengemudi Baru</button></div>			<input type="text" id="inputNomorPengemudi" class="span3" data-provide="typeahead" placeholder="Cari Pengemudi">			<table class="table table-bordered table-hover">				<thead>					<tr>						<th>ID Pengemudi</th>						<th>Nama Lengkap</th>						<th>Alamat</th>						<th>Jenis SIM</th>						<th>TGL Gabung</th>						<th width='15%'>Aksi</th>					</tr>				</thead>								<tbody>					<? foreach ($this->listPengemudi as $key => $value) { ?>						<tr id="pengemudi-<? echo $value['id']; ?>">							<td><span class='label label-info'><? echo $value['nomor']; ?></span></td>							<td><? echo $value['nama']; ?></td>							<td><? echo $value['alamat']; ?></td>							<td><span class='label label-info'><? echo $value['jenissim']; ?></span></td>							<td><? echo date('d-m-Y', strtotime($value['tglgabung'])) ?></td>							<td>								<div class="btn-group">									<a class="btn btn-primary" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>									<a class="btn btn-primary dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>									<ul class="dropdown-menu">										<li>											<a href="javascript:void(0)" onClick="return details(<? echo $value['id']; ?>)">											<i class="icon-list-alt"></i> Details</a>										</li>										<? if($value['aktif'] == 1) : ?>											<li>												<a href="javascript:void(0)" onClick="return nonaktif(<? echo $value['id']; ?>)">												<i class="icon-off"></i> Non Aktifkan</a>											</li>										<? else: ?>											<li>												<a href="javascript:void(0)" onClick="return aktif(<? echo $value['id']; ?>)">												<i class="icon-ok"></i> Aktifkan</a>											</li>										<? endif; ?>										<li>											<a href="javascript:void(0)" onClick="return remove(<? echo $value['id']; ?>)">											<i class="icon-remove"></i> Hapus</a>										</li>									</ul>								</div>							</td>						</tr>					<? } ?>				</tbody>			</table>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#inputNomorPengemudi').typeahead({			source : function(query,process){				return $.post(site+'sro/getlistpengemudi', {param : query}, function(response){					response = $.parseJSON(response);					var sourceArr = [];						for(var i = 0; i < response.length; i++)					{						sourceArr.push(response[i].id +"#"+ response[i].nomor +"#"+ response[i].nama +"#"+ response[i].foto);					}					return process(sourceArr);				})			},			highlighter : function(item){				var pengemudi = item.split('#');				var item = ''                		+ "<div class='typeahead_wrapper'>"                		+ "<img class='typeahead_photo' src='"+site+"assets/img/pengemudi/"+pengemudi[3]+"'/>"                		+ "<div class='typeahead_labels'>"                		+ "<div class='typeahead_primary'>" + pengemudi[1] + "</div>"                 		+ "<div class='typeahead_secondary'>Nama : " + pengemudi[2] + "</div>"                 		+ "</div>"                 		+ "</div>";       		 	return item;			},			updater : function(item){				var pengemudi = item.split('#');				load('pengemudi/detail/'+pengemudi[0], '#content');			}		});	})	function details(id)	{		load('pengemudi/detail/'+id, '#content');	}	function aktif(id)	{		$.post(site+'pengemudi/aktif', {id : id}, function(){				window.alert('Pengemudi Telah Diaktifkan');				load('pengemudi/detail/'+id, '#content');			});	}	function nonaktif(id)	{		$.post(site+'pengemudi/nonaktif', {id : id}, function(){				window.alert('Pengemudi Telah Dinonaktifkan');				load('pengemudi/detail/'+id, '#content');			});	}	function remove(id)	{		if(confirm("Anda Yakin Akan Menghapus Pengemudi Ini? Semua Data Berkaitan dengan Pengemudi ybs Akan Ikut Terhapus."))		{			$.post(site+'pengemudi/delete', {id : id}, function(){				$('#pengemudi-'+id).remove();			});		}	}</script>