<? foreach ($this->listPerawatan as $key => $value) { ?>	<tr id="perawatan-<? echo $value['id']; ?>">		<td><? echo $value['nomor']; ?></td>		<td><? echo $value['nomorunit']; ?></td>		<td><? echo date('d-m-Y', strtotime($value['tanggal'])); ?></td>		<td><? echo $value['detail']; ?></td>		<td><? echo $this->pecah($value['biayaperawatan']); ?></td>		<td><? echo $this->pecah($value['biayaparts']); ?></td>		<td>			<div class="btn-group">				<a class="btn btn-primary" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>				<a class="btn btn-primary dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>				<ul class="dropdown-menu">					<li>						<a href="javascript:void(0)" onClick="return showDetail(<? echo $value['id']; ?>)">						<i class="icon-pencil"></i> Detail</a>					</li>					<li>						<a href="javascript:void(0)" onClick="return remove(<? echo $value['id']; ?>)">						<i class="icon-remove"></i> Hapus</a>					</li>				</ul>			</div>		</td>	</tr><? } ?>