<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h1 class="text-info">Rekap Bulanan Armada</h1>		</div>		<div class="well">			<div class="pull-right">				<button class="btn btn-primary" onClick="printArea()"><i class="icon-print icon-white"></i> Cetak</button>			</div>			<input type="text" id="filterArmada" data-provide="typeahead" placholder="Ketikkan ID Armada/Nama">			<input type="hidden" id="idArmada" value="0">			<select id="filterBulan" onchange="filterData()">				<option value="1">Januari</option>				<option value="2">Februari</option>				<option value="3">Maret</option>				<option value="4">April</option>				<option value="5">Mei</option>				<option value="6">Juni</option>				<option value="7">Juli</option>				<option value="8">Agustus</option>				<option value="9">September</option>				<option value="10">Oktober</option>				<option value="11">Nopember</option>				<option value="12">Desember</option>			</select>			<script type="text/javascript">				var d = new Date();				var m = d.getMonth() + 1;				$('#filterBulan').val(m);			</script>			<br>			<div id="printArea">											</div>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#filterArmada').typeahead({			source : function(query,process){				return $.post(site+'sro/getlistarmada', {nomor : query}, function(response){					response = $.parseJSON(response);					var sourceArr = [];						for(var i = 0; i < response.length; i++)					{						sourceArr.push(response[i].id +"#"+ response[i].nomorunit +"#"+ response[i].kondisi);					}					return process(sourceArr);				})			},			highlighter : function(item){				var armada = item.split('#');				var item = ''                		+ "<div class='typeahead_wrapper'>"                		+ "<img class='typeahead_photo' src='"+site+"assets/img/car-icon.png'/>"                		+ "<div class='typeahead_labels'>"                		+ "<div class='typeahead_primary'>" + armada[1] + "</div>"                 		+ "<div class='typeahead_secondary'>Kondisi : " + capitalizeFirstLetter(armada[2]) + "</div>"                 		+ "</div>"                 		+ "</div>";       		 	return item;			},			updater : function(item){				var armada = item.split('#');				$('#idArmada').val(armada[0]);				var bulan = $('#filterBulan').val();				load('report/filterarmada/'+armada[0]+'_'+bulan,'#printArea');				return armada[1];			}		});	})function filterData(){	var armada = $('#idArmada').val();	var bulan = $('#filterBulan').val();	load('report/filterarmada/'+armada+'_'+bulan,'#printArea');}</script>