<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h1 class="text-info">History Kurang Setoran Pengemudi</h1>		</div>		<div class="well">			<div class="pull-right">				<button class="btn btn-primary" onClick="printArea()"><i class="icon-print icon-white"></i> Cetak</button>			</div>			<input type="text" id="filterPengemudi" data-provide="typeahead" placholder="Ketikkan ID Pengemudi/Nama">			<br>			<div id="printArea">											</div>		</div>	</div></div><script type="text/javascript">	$(function(){		$('#filterPengemudi').typeahead({			source : function(query,process){				return $.post(site+'sro/getlistpengemudi', {param : query}, function(response){					response = $.parseJSON(response);					var sourceArr = [];						for(var i = 0; i < response.length; i++)					{						sourceArr.push(response[i].id +"#"+ response[i].nomor +"#"+ response[i].nama +"#"+ response[i].foto);					}					return process(sourceArr);				})			},			highlighter : function(item){				var pengemudi = item.split('#');				var item = ''                		+ "<div class='typeahead_wrapper'>"                		+ "<img class='typeahead_photo' src='"+site+"assets/img/pengemudi/"+pengemudi[3]+"'/>"                		+ "<div class='typeahead_labels'>"                		+ "<div class='typeahead_primary'>" + pengemudi[1] + "</div>"                 		+ "<div class='typeahead_secondary'>Nama : " + pengemudi[2] + "</div>"                 		+ "</div>"                 		+ "</div>";       		 	return item;			},			updater : function(item){				var pengemudi = item.split('#');				load('report/historykspengemudi/'+pengemudi[0],'#printArea');				return pengemudi[1]+'-'+pengemudi[2];			}		});	})</script>