<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h1 class="text-info">Rekap Periodik Pengemudi</h1>		</div>		<div class="well">			<div class="pull-right">				<button class="btn btn-primary" onClick="printArea()"><i class="icon-print icon-white"></i> Cetak</button>			</div>			<button class="btn btn-success" onClick="showFormFilter()"><i class="icon-filter icon-white"></i> Filter</button>			<!--<select id="filterBulan" onchange="filterData()">				<option value="1">Januari</option>				<option value="2">Februari</option>				<option value="3">Maret</option>				<option value="4">April</option>				<option value="5">Mei</option>				<option value="6">Juni</option>				<option value="7">Juli</option>				<option value="8">Agustus</option>				<option value="9">September</option>				<option value="10">Oktober</option>				<option value="11">Nopember</option>				<option value="12">Desember</option>			</select>			<script type="text/javascript">				var d = new Date();				var m = d.getMonth() + 1;				$('#filterBulan').val(m);			</script>			<br>-->			<div id="printArea">											</div>		</div>	</div></div><!-- Form Add transaksi [modal]===================================== --><div id="formFilter" class="modal hide fade" tabindex="-1" aria-hidden="true">	<div class="modal-header">		<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>		<h3 id="myModalLabel">Filter Rekap Pengemudi</h3>	</div>	<div class="modal-body">		<form class="form-horizontal">			<div class="control-group">				<label class="control-label" for="inputJenisTransaksi">Pilih Pengemudi</label>				<div class="controls">					<input type="text" id="filterPengemudi" data-provide="typeahead" placholder="Ketikkan ID Pengemudi/Nama">					<input type="hidden" id="idPengemudi" value="0">				</div>			</div>				<div class="control-group">				<label class="control-label" for="inputPosting">Dari Tanggal</label>				<div class="controls">					<input type="text" id="filterTglAwal" placeholder="Tgl Awal" value="<? echo date('Y-m-d'); ?>" class="span2">				</div>			</div>			<div class="control-group">				<label class="control-label" for="inputPosting">Sampai Tanggal</label>				<div class="controls">					<input type="text" id="filterTglAkhir" placeholder="Tgl Akhir" value="<? echo date('Y-m-d'); ?>" class="span2">				</div>			</div>		</form>	</div>	<div class="modal-footer">		<button class="btn" data-dismiss="modal" aria-hidden="true">Batal</button>		<button class="btn btn-primary" onClick="filterPengemudi()" data-dismiss="modal" aria-hidden="true">Proses</button>	</div></div><!-- End of Form Add transaksi [modal] --><script type="text/javascript">	$(function(){		$('#filterTglAwal').datepicker({dateFormat : "yy-mm-dd"});		$('#filterTglAkhir').datepicker({dateFormat : "yy-mm-dd"});		$('#filterPengemudi').typeahead({			source : function(query,process){				return $.post(site+'sro/getlistpengemudi', {param : query}, function(response){					response = $.parseJSON(response);					var sourceArr = [];						for(var i = 0; i < response.length; i++)					{						sourceArr.push(response[i].id +"#"+ response[i].nomor +"#"+ response[i].nama +"#"+ response[i].foto);					}					return process(sourceArr);				})			},			highlighter : function(item){				var pengemudi = item.split('#');				var item = ''                		+ "<div class='typeahead_wrapper'>"                		+ "<img class='typeahead_photo' src='"+site+"assets/img/pengemudi/"+pengemudi[3]+"'/>"                		+ "<div class='typeahead_labels'>"                		+ "<div class='typeahead_primary'>" + pengemudi[1] + "</div>"                 		+ "<div class='typeahead_secondary'>Nama : " + pengemudi[2] + "</div>"                 		+ "</div>"                 		+ "</div>";       		 	return item;			},			updater : function(item){				var pengemudi = item.split('#');				$('#idPengemudi').val(pengemudi[0]);				return pengemudi[1]+'-'+pengemudi[2];			}		});	})	function filterPengemudi()	{		var pengemudi = $('#idPengemudi').val();		var awal = $('#filterTglAwal').val();		var akhir = $('#filterTglAkhir').val();		load('report/filterpengemudi/'+pengemudi+'_'+awal+'_'+akhir,'#printArea');		$('#filterPengemudi').val('');		$('#idPengemudi').val('0');	}	function showFormFilter()	{		$('#formFilter').modal('show');	}</script>