<!-- Main Row =============================================== --><div class="container-fluid">	<div class="row-fluid">				<div class="well">			<h1 class="text-info">Laporan SRO Terbit</h1>		</div>		<div class="well">			<div class="pull-right">				<button class="btn btn-primary" onClick="printArea()"><i class="icon-print icon-white"></i> Cetak</button>			</div>			<input type="text" id="filterTanggal" placeholder="Pilih Tanggal Untuk Filter" value="<? echo date('Y-m-d'); ?>">						<select id="filterReport" class="span3" onChange="filterKlasifikasi()">				<option value="0">--Pilih Klasifikasi</option>				<option value="1">Armada Umum Gowata</option>				<option value="2">Armada Umum 3G</option>				<option value="3">Armada Bandara Gowata</option>				<option value="4">Armada Bandara 3G</option>			</select>			<br>			<div id="printArea">				<div class="reportTitle">					<h4>PD Perdagangan Umum Unit Taksi Gowata</h4>					<h4>PT. Gempita Gemintang Gemilang (3G)</h4>					==============================================					<p>Laporan Armada Keluar Periode : <? echo date('d-m-Y', strtotime($this->currDate)); ?></p>					<? switch ($this->currKlasifikasi) {						case '1':							?><p>Armada Gowata</p><?							break;						case '2':							?><p>Armada 3G</p><?							break;												case '3':							?><p>Armada Bandara Gowata</p><?							break;						case '4':							?><p>Armada Bandara 3G</p><?							break;						default:							?><p>Semua Armada</p><?							break;					} ?>				</div>				<p>Total Armada Keluar : <? echo count($this->listSRO); ?></p>								<table class="table table-bordered">					<thead>						<tr>							<th>Nomor</th>							<th width="25%">Nomor SRO</th>							<th>Tanggal</th>							<th>Nomor Unit</th>							<th>ID Pengemudi</th>							<th>Nama Pengemudi</th>							<th>Jam Keluar</th>						</tr>					</thead>										<tbody>						<? $nomor = 0; ?>						<? foreach ($this->listSRO as $key => $value) { ?>						<? $nomor += 1; ?>							<tr id="sro-<? echo $value['id']; ?>">								<td><? echo $nomor; ?></td>								<td><? echo $value['nomor']; ?></td>								<td><? echo date('d-m-Y', strtotime($value['tanggal'])); ?></td>								<td><? echo $value['nomorunit']; ?></td>								<td><? echo $value['idpengemudi']; ?></td>								<td><? echo $value['pengemudi']; ?></td>								<td><? echo date('H:i:s', strtotime($value['tanggal'])); ?></td>							</tr>						<? } ?>					</tbody>				</table>			</div>					</div>	</div></div><script type="text/javascript">	$(function(){		$('#filterTanggal').datepicker({			dateFormat : "yy-mm-dd",			onSelect : function(){				filterKlasifikasi();			}		});	})	function filterKlasifikasi()	{		var report = $('#filterReport').val();		var tanggal = $('#filterTanggal').val();		load('report/filtersro/'+tanggal+'_'+report, '#printArea');	}</script>