<input type="hidden" id="statusButton" value="<? echo $this->buttonStatus; ?>"><? foreach ($this->listSRO as $key => $value) { ?>	<tr id="sro-<? echo $value['id']; ?>">		<td><? echo $value['nomor']; ?></td>		<td><strong class="text-info"><? echo $value['nomorunit']; ?></strong></td>		<td><strong class="text-success"><? echo $value['pengemudi']; ?></strong></td>		<td><? echo date('H:i:s', strtotime($value['tanggal'])); ?></td>		<td>			<div class="btn-group">				<a class="btn btn-primary" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>				<a class="btn btn-primary dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>				<ul class="dropdown-menu">					<li>						<? if(date('d-m-Y', strtotime($value['tanggal'])) == date('d-m-Y')) : ?>							<a href="javascript:void(0)" onClick="return remove(<? echo $value['id']; ?>)">							<i class="icon-remove"></i> Batalkan</a>						<? endif; ?>					</li>				</ul>			</div>		</td>	</tr><? } ?>