<? foreach ($this->listTransaksi as $key => $value) { ?>	<tr id="transaksi-<? echo $value['id']; ?>">		<td><? echo $value['nomor']; ?></td>		<td><? echo $value['jenis']; ?></td>		<td><? echo $value['bentuk']; ?></td>		<td><? echo date('d-m-Y', strtotime($value['tanggal'])); ?></td>		<td><? echo $this->pecah($value['nilai']); ?></td>		<td>			<input type="hidden" id="nomor-<? echo $value['id']; ?>" value="<? echo $value['nomor']; ?>">				<input type="hidden" id="jenis-<? echo $value['id']; ?>" value="<? echo $value['jenis']; ?>">				<input type="hidden" id="jenistransaksi-<? echo $value['id']; ?>" value="<? echo $value['jenistransaksi']; ?>">				<input type="hidden" id="posting-<? echo $value['id']; ?>" value="<? echo $value['posting']; ?>">				<input type="hidden" id="bentuk-<? echo $value['id']; ?>" value="<? echo $value['bentuk']; ?>">				<input type="hidden" id="tanggal-<? echo $value['id']; ?>" value="<? echo $value['tanggal']; ?>">				<input type="hidden" id="nilai-<? echo $value['id']; ?>" value="<? echo $value['nilai']; ?>">				<input type="hidden" id="keterangan-<? echo $value['id']; ?>" value="<? echo $value['keterangan']; ?>">				<div class="btn-group">				<a class="btn btn-primary" href="javascript:void(0)"><i class="icon-edit icon-white"></i> Aksi</a>				<a class="btn btn-primary dropdown-toggle" data-toggle="dropdown" href="javascript:void(0)"><span class="caret"></span></a>				<ul class="dropdown-menu">					<li>						<a href="javascript:void(0)" onClick="return showFormDetail(<? echo $value['id']; ?>)">						<i class="icon-list-alt"></i> Detail</a>					</li>					<li>						<a href="javascript:void(0)" onClick="return showFormUpdate(<? echo $value['id']; ?>)">						<i class="icon-pencil"></i> Update Transaksi</a>					</li>					<li>						<a href="javascript:void(0)" onClick="return remove(<? echo $value['id']; ?>)">						<i class="icon-remove"></i> Hapus</a>					</li>				</ul>			</div>		</td>	</tr><? } ?>