<?php/** Class Form * - Fill out a form *	- POST to PHP *  - Sanitize *  - Validate *  - Return Data *  - Write to Database Location : ../core/Form.php */  require 'Val.php';  class Form {	private $_currentItem = NULL;	private $_postData = array();	private $_var = array();	private $_error = array();		public function __construct()	{		$this->_val = new Val();	}		public function post($field)	{		$this->_postData[$field] = $_POST[$field];		$this->_currentItem[$field] = $field;				return $this;	}		public function fetch($fieldName = false)	{		if($fieldName)		{			if(isset($this->_postData[$fieldName]))				return $this->_postData[$fieldName];			else				return false;		}		else		{			return $this->_postData;		}	}		public function val($typeOfValidator, $arg = NULL)	{		if($arg == NULL)			$error = $this->_val->{$typeOfValidator}($this->_postData[$this->_currentItem]);		else			$error = $this->_val->{$typeOfValidator}($this->_postData[$this->_currentItem], $arg);				if($error)			$this->_error[$this->_currentItem] = $error;				return $this;	}		public function submit()	{		if(empty($this->_error))		{			return true;		}		else		{			$str = '';			foreach($this->_error as $key => $value)			{				$str .=  $key .' => '. $value . "\n";			}			throw new Exception($str);		}	}}//End of Class