<?phpclass View{	function __construct()	{			}		public function render($name, $noInclude = false)	{		if($noInclude == true)		{			require APP . 'views/'. $name .'.php';		}		else		{			require APP . 'views/header.php';            require APP . 'views/'. $name .'.php';            require APP . 'views/footer.php';		}	}	function pecah($uang,$delimiter='.',$kurung=false)    {        if($uang == '' || $uang == 0)        {            $rupiah = '0';            return $rupiah;        }        $neg = false;        if($uang<0)        {            $neg = true;            $uang = abs($uang);        }                $rupiah = number_format($uang,0,',',$delimiter);        $rupiah = 'Rp'.$rupiah.',-';        if($neg && $kurung)        {            $rupiah = '('.$rupiah.')';        }        if($neg && $kurung)        {            $rupiah = '<font color="red">-'.$rupiah.'</font>';        }        return $rupiah;    }}